import './App.css';
import Container from './new/Container';

function App() {
  function setName(name) {
    localStorage.setItem("name", name);
  }


  return (
    <div className="App">
      <p>Hello, {localStorage.getItem("name")}</p>
      <input type="text" id="nameBox"/>
      <button onClick={() => localStorage.setItem("name", document.getElementById("nameBox").value)}>Submit Name</button>
      <Container/>
    </div>
  );
}

export default App;
