import React from 'react';
import {useState} from 'react';
import {FaTrashAlt} from 'react-icons/fa';
import TaskList from './TaskList';
const MakeGroup = () => {
  const [tasks,setTasks] = useState([
    {
      id: 1,
      checked: true,
      taskName : "Physics Electrodynamics HW-1",
      dueDate: "Friday",  
      description: "It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using 'Content here, content here', making it look like readable English. Many desktop publishing",
      weight: 1
    },
    {
      id: 2,
      checked: false,
      taskName : "Minesweeper Computer Science Project ",
      dueDate: "Sunday",
      description: "It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using 'Content here, content here', making it look like readable English. Many desktop publishing",
      weight: 3
    },
    {
      id: 3,
      checked: false,
      taskName : "Statistics HW",
      dueDate: "Friday",
      description: "The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using 'Content here, content here', making it look like readable English. Many desktop publishing",
      weight: 1
    }
  ]);
  
  const handleCheck = (id) => {
    const listTasks = tasks.map((task) => task.id === id ? { ...task,
    checked: !task.checked} : task);
    setTasks(listTasks);
    localStorage.setTasks("tasksList",JSON.stringify(listTasks));
  }
  
  const handleDelete = (id) => {
    const listTasks = tasks.filter((task) => task.id !== id);
    setTasks(listTasks);
    localStorage.setTasks("tasksList",JSON.stringify(listTasks));
  }

  return (
    <div>
        <TaskList
          tasks={tasks}
          handleCheck={handleCheck}
          handleDelete={handleDelete}
        />
    </div>
  )
}

export default MakeGroup
