});

const Room = () => {
  const {id} = useParams();
  const [rooms, setRooms] = useState([]);
  const [flag, setFlag] = useState(0);
  const room = rooms.find(room => room.password === id)
  
  useEffect(() => {
    client.get()
    .then((res) => {
      setRooms(res.data);
    })
  }, [flag]);


  return (
    <body>
      <Link to="/">{id}</Link>
      
    </body>
  )
}

