import React from 'react'
import axios from "axios";
import {useEffect, useState} from "react";
const mongoose = require("mongoose");

const client = axios.create({
  baseURL: "http://localhost:3500/rooms" 
});

const Container = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    client.get()
    .then((res) => {
      setPosts(res);
      console.log(posts)
    })
  }, []);

  function createPost() {
    client.post("", {
      id: posts.length,
      name: "createPost test",
      password: "password123"
    })
    .then((res) => {
      setPosts([...posts, res.data]);
    })
  }

  function updatePost(id) {
    client.put(`/${id}`, {
      title: "Hello world",
      body: "This is an updated post"
    })
    .then((res) => {
      setPosts(res.data)
    })
  }

  function deletePost(id) {
    client.delete(`/${id}`)
    .then(() => {
      //setPosts(null);
    })
  }

  if (posts.length == 0) return "No posts!";

  return (
    <div>
      <ul>
        {posts.map((post) => {
          <>
            <h1>{post.name}</h1>
            <p>{post.id}</p>
          </>
        })}
      </ul>
      <button onClick={createPost}>Create a post</button>
      <button onClick={updatePost(0)}>Update post</button>
      <button onClick={deletePost}>Delete Post</button>
    </div>
  )
}

export default Container
