import React from 'react'
import axios from "axios";
import {useEffect, useState} from "react";
const mongoose = require("mongoose");

const client = axios.create({
  baseURL: "http://localhost:3500/rooms" 
});

const Container = () => {
  let updateFlag = 0;
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    client.get()
    .then((res) => {
      setRooms(res.data);
    })
  }, [updateFlag]);

  function createRoom() {
    client.post("", {
      id: rooms.length,
      name: "createRoom test",
      password: "password123"
    })
    .then((res) => {
      //setRooms([...rooms, res.data]);
      updateFlag++;
    })
  }

  function updateRoom(paramId) {
    client.put(`/${paramId}`, {
      id: paramId,
      name: "updateRoom test",
      password: "password1234"
    })
    .then((res) => {
      const myRoom = rooms.findIndex(room => room.id === paramId)
      console.log(myRoom)
      console.log(rooms)
    })
  }

  function deleteRoom(id) {
    client.delete(`/${id}`)
    .then(() => {
      //setRooms(null);
    })
  }

  if (rooms.length == 0) return "No rooms!";

  return (
    <div>
      <ul>
        {rooms.map((room) => (
          <>
            <h1>{room.name}</h1>
            <p>{room.id}</p>
          </>
        ))}
      </ul>
      <button onClick={createRoom}>Create a room</button>
      <button onClick={() => updateRoom()}>Update room</button>
      <button onClick={deleteRoom}>Delete Room</button>
    </div>
  )
}

export default Container
