import React from 'react'
import axios from "axios";
import {useEffect, useState} from "react";
const mongoose = require("mongoose");

const client = axios.create({
  baseURL: "http://localhost:3500/rooms" 
});

const Container = () => {
  const [rooms, setRooms] = useState([]);
  const [flag, setFlag] = useState(0);

  useEffect(() => {
    client.get()
    .then((res) => {
      setRooms(res.data);
    })
  }, [flag]); //no dependency here allows commenting of request result actions
     //add time-polling solution here

  function createRoom() {
    client.post("", {
      id: rooms[rooms.length-1].id + 1,
      name: "createRoom test",
      password: "password123",
      creator: localStorage.getItem("name") //MAKE THIS MORE SECURE
    })
    .then((res) => {
      //setRooms([...rooms, res.data]);
      setFlag(flag + 1);
    })  
  }

  function updateRoom(paramId) {
    client.put(`/${paramId}`, {
      id: paramId,
      name: "updateRoom test",
      password: "password1234"
    })
    .then((res) => {
      //const myRoom = rooms.findIndex(room => room.id === paramId)
      setFlag(flag + 1);
    })
  }

  function deleteRoom(id) {
    client.delete(`/${id}`)
    .then(() => {
      //setRooms(null);
      setFlag(flag + 1);
    })
  }

  if (rooms.length == 0) return "No rooms!";

  return (
    <div>
      <ul>
        {rooms.map((room) => (
          <>
            <h1>{room.name}</h1>
            <p>{room.id}</p>
          </>
        ))}
      </ul>
      <input type="text" id="nameBox" placeholder='Room name'/>
      <input type="text" id="nameBox" placeholder='Room password'/>
      <button onClick={createRoom}>Create a room</button>
      <button onClick={() => updateRoom(7)}>Update room</button>
      <button onClick={() => deleteRoom(8)}>Delete Room</button>
      <button onClick={() => console.log(rooms)}>print Rooms</button>
    </div>
  )
}

export default Container
